        <span class="token class-name">System</span><span class="token punctuation">.</span>out<span class="token punctuation">.</span><span class="token function">println</span><span class="token punctuation">(</span><span class="token string">"受影响的行数:"</span><span class="token operator">+</span>i1<span class="token punctuation">)</span><span class="token punctuation">;</span>
