  }
<span class="token operator">&lt;</span><span class="token operator">/</span>style<span class="token operator">></span>
<span class="token operator">&lt;</span>body<span class="token operator">></span>
  <span class="token operator">&lt;</span><span class="token operator">div</span> id<span class="token operator">=</span><span class="token string">"app"</span><span class="token operator">></span>
    <span class="token operator">&lt;</span>p :class<span class="token operator">=</span><span class="token string">"{red: number>=5&amp;&amp; number&lt;10,pink: number>=10}"</span><span class="token operator">></span>{{number}}<span class="token operator">&lt;</span><span class="token operator">/</span>p<span class="token operator">></span>
    <span class="token operator">&lt;</span>button <span class="token variable">@click</span><span class="token operator">=</span><span class="token string">"add()"</span><span class="token operator">></span><span class="token operator">+</span><span class="token number">1</span><span class="token operator">&lt;</span><span class="token operator">/</span>button<span class="token operator">></span>
    <span class="token operator">&lt;</span>button <span class="token variable">@click</span><span class="token operator">=</span><span class="token string">'reduce()'</span><span class="token operator">></span><span class="token operator">-</span><span class="token number">1</span><span class="token operator">&lt;</span><span class="token operator">/</span>button<span class="token operator">></span>
    <span class="token operator">&lt;</span>P :style<span class="token operator">=</span><span class="token string">"{width:100+number+'px'}"</span><span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>p<span class="token operator">></span>
  <span class="token operator">&lt;</span><span class="token operator">/</span><span class="token operator">div</span><span class="token operator">></span>
<span class="token operator">&lt;</span><span class="token operator">/</span>body<span class="token operator">></span>
<span class="token operator">&lt;</span>script <span class="token keyword">type</span><span class="token operator">=</span><span class="token string">"text/javascript"</span><span class="token operator">></span>
  new Vue<span class="token punctuation">(</span>{
    el:<span class="token string">'#app'</span><span class="token punctuation">,</span>
    <span class="token keyword">data</span>:{
      number:<span class="token number">0</span>
    }<span class="token punctuation">,</span>
    methods:{
      <span class="token keyword">add</span><span class="token punctuation">(</span><span class="token punctuation">)</span>{
        this<span class="token punctuation">.</span>number<span class="token operator">+</span><span class="token operator">+</span><span class="token punctuation">;</span>
