import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as d,c as e,f as r}from"./app-a48529f7.js";const a={},h=r('<h2 id="mysql的常用函数" tabindex="-1"><a class="header-anchor" href="#mysql的常用函数" aria-hidden="true">#</a> MySQL的常用函数</h2><h3 id="字符串函数" tabindex="-1"><a class="header-anchor" href="#字符串函数" aria-hidden="true">#</a> 字符串函数</h3><h4 id="连接" tabindex="-1"><a class="header-anchor" href="#连接" aria-hidden="true">#</a> 连接</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>CONCAT(S1,S2,......,Sn)</td><td>连接S1,S2,......,Sn为一个字符串</td></tr><tr><td>CONCAT_WS(separator, S1,S2,......,Sn)</td><td>连接S1一直到Sn，并且中间以separator作为分隔符</td></tr></tbody></table><h4 id="长度" tabindex="-1"><a class="header-anchor" href="#长度" aria-hidden="true">#</a> 长度</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>CHAR_LENGTH(s)</td><td>返回字符串s的字符数</td></tr><tr><td>LENGTH(s)</td><td>返回字符串s的字节数，和字符集有关</td></tr></tbody></table><h4 id="替换" tabindex="-1"><a class="header-anchor" href="#替换" aria-hidden="true">#</a> 替换</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>INSERT(str, index , len, instr)</td><td>将字符串str从第index位置开始，len个字符长的子串替换为字符串instr</td></tr></tbody></table><h4 id="大小写处理" tabindex="-1"><a class="header-anchor" href="#大小写处理" aria-hidden="true">#</a> 大小写处理</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>UPPER(s) 或 UCASE(s)</td><td>将字符串s的所有字母转成大写字母</td></tr><tr><td>LOWER(s) 或LCASE(s)</td><td>将字符串s的所有字母转成小写字母</td></tr></tbody></table><h4 id="空格处理" tabindex="-1"><a class="header-anchor" href="#空格处理" aria-hidden="true">#</a> 空格处理</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>LTRIM(s)</td><td>去掉字符串s左侧的空格</td></tr><tr><td>RTRIM(s)</td><td>去掉字符串s右侧的空格</td></tr><tr><td>TRIM(s)</td><td>去掉字符串s开始与结尾的空格</td></tr></tbody></table><h4 id="其他处理" tabindex="-1"><a class="header-anchor" href="#其他处理" aria-hidden="true">#</a> 其他处理</h4><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>LEFT(s,n)</td><td>返回字符串s最左边的n个字符</td></tr><tr><td>RIGHT(s,n)</td><td>返回字符串s最右边的n个字符</td></tr><tr><td>LPAD(str, len, pad)</td><td>用字符串pad对str最左边进行填充，直到str的长度为len个字符</td></tr><tr><td>RPAD(str ,len, pad)</td><td>用字符串pad对str最右边进行填充，直到str的长度为len个字符</td></tr><tr><td>TRIM(【BOTH 】s1 FROM s)</td><td>去掉字符串s开始与结尾的s1</td></tr><tr><td>TRIM(【LEADING】s1 FROM s)</td><td>去掉字符串s开始处的s1</td></tr><tr><td>TRIM(【TRAILING】s1 FROM s)</td><td>去掉字符串s结尾处的s1</td></tr><tr><td>REPEAT(str, n)</td><td>返回str重复n次的结果</td></tr><tr><td>REPLACE（str, a, b）</td><td>用字符串b替换字符串str中所有出现的字符串a</td></tr><tr><td>STRCMP(s1,s2)</td><td>比较字符串s1,s2</td></tr><tr><td>SUBSTRING(s,index,len)</td><td>返回从字符串s的index位置其len个字符</td></tr></tbody></table><h3 id="数值函数" tabindex="-1"><a class="header-anchor" href="#数值函数" aria-hidden="true">#</a> 数值函数</h3><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td>ABS(x)</td><td>返回x的绝对值</td></tr><tr><td>CEIL(x)</td><td>返回大于x的最小整数值</td></tr><tr><td>FLOOR(x)</td><td>返回小于x的最大整数值</td></tr><tr><td>MOD(x,y)</td><td>返回x/y的模</td></tr><tr><td>RAND()</td><td>返回0~1的随机值</td></tr><tr><td>ROUND(x,y)</td><td>返回参数x的四舍五入的有y位的小数的值</td></tr><tr><td>TRUNCATE(x,y)</td><td>返回数字x截断为y位小数的结果</td></tr><tr><td>SQRT(x)</td><td>返回x的平方根</td></tr><tr><td>POW(x,y)</td><td>返回x的y次方</td></tr></tbody></table><h3 id="日期函数" tabindex="-1"><a class="header-anchor" href="#日期函数" aria-hidden="true">#</a> 日期函数</h3><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td><code>CURDATE()</code> 或 <code>CURRENT_DATE()</code></td><td>返回当前日期</td></tr><tr><td><code>CURTIME()</code> 或 <code>CURRENT_TIME()</code></td><td>返回当前时间</td></tr><tr><td><code>NOW()</code> / <code>SYSDATE()</code> / <code>CURRENT_TIMESTAMP()</code> / <code>LOCALTIME()</code> / <code>LOCALTIMESTAMP()</code></td><td>返回当前系统日期时间</td></tr><tr><td><code>YEAR(date)</code> / <code>MONTH(date)</code> / <code>DAY(date)</code> / <code>HOUR(time)</code> / <code>MINUTE(time)</code> / <code>SECOND(time)</code></td><td>返回具体的时间值</td></tr><tr><td><code>WEEK(date)</code> / <code>WEEKOFYEAR(date)</code></td><td>返回一年中的第几周</td></tr><tr><td><code>DAYOFWEEK()</code></td><td>返回今天是一周的第几天，注意：周日是1，周一是2，。。。周六是7</td></tr><tr><td><code>WEEKDAY(date)</code></td><td>返回周几，注意，周1是0，周2是1，。。。周日是6</td></tr><tr><td><code>DAYNAME(date)</code></td><td>返回星期：MONDAY,TUESDAY.....SUNDAY</td></tr><tr><td><code>MONTHNAME(date)</code></td><td>返回月份：January，。。。。。</td></tr><tr><td><code>DATEDIFF(date1,date2)</code> / <code>TIMEDIFF(time1, time2) </code></td><td>返回date1 - date2的日期间隔 / 返回time1 - time2的时间间隔</td></tr><tr><td><code>DATE_ADD(datetime, INTERVAL expr type) </code></td><td>返回与给定日期时间相差INTERVAL时间段的日期时间</td></tr><tr><td><code>DATE_FORMAT(datetime ,fmt) </code></td><td>按照字符串fmt格式化日期datetime值</td></tr><tr><td><code>STR_TO_DATE(str, fmt)</code></td><td>按照字符串fmt对str进行解析，解析为一个日期</td></tr></tbody></table><p>其中：</p><p><strong>DATE_ADD(datetime,INTERVAL expr type)</strong> 表达式类型：</p><table><thead><tr><th>参数类型</th><th>参数类型</th></tr></thead><tbody><tr><td>YEAR</td><td>YEAR_MONTH</td></tr><tr><td>MONTH</td><td>DAY_HOUR</td></tr><tr><td>DAY</td><td>DAY_MINUTE</td></tr><tr><td>HOUR</td><td>DAY_SECOND</td></tr><tr><td>MINUTE</td><td>HOUR_MINUTE</td></tr><tr><td>SECOND</td><td>HOUR_SECOND</td></tr><tr><td></td><td>MINUTE_SECOND</td></tr></tbody></table><p><strong>DATE_FORMAT(datetime,fmt) 和 STR_TO_DATE(str, fmt)</strong> 表达式类型：</p><table><thead><tr><th>格式符</th><th>说明</th><th>格式符</th><th>说明</th></tr></thead><tbody><tr><td>%Y</td><td>4位数字表示年份</td><td>%y</td><td>表示两位数字表示年份</td></tr><tr><td>%M</td><td>月名表示月份（January,....）</td><td>%m</td><td>两位数字表示月份（01,02,03。。。）</td></tr><tr><td>%b</td><td>缩写的月名（Jan.，Feb.，....）</td><td>%c</td><td>数字表示月份（1,2,3,...）</td></tr><tr><td>%D</td><td>英文后缀表示月中的天数（1st,2nd,3rd,...）</td><td>%d</td><td>两位数字表示月中的天数(01,02...)</td></tr><tr><td>%e</td><td>数字形式表示月中的天数（1,2,3,4,5.....）</td><td></td><td></td></tr><tr><td>%H</td><td>两位数字表示小数，24小时制（01,02..）</td><td>%h和%I</td><td>两位数字表示小时，12小时制（01,02..）</td></tr><tr><td>%k</td><td>数字形式的小时，24小时制(1,2,3)</td><td>%l</td><td>数字形式表示小时，12小时制（1,2,3,4....）</td></tr><tr><td>%i</td><td>两位数字表示分钟（00,01,02）</td><td>%S和%s</td><td>两位数字表示秒(00,01,02...)</td></tr><tr><td>%W</td><td>一周中的星期名称（Sunday...）</td><td>%a</td><td>一周中的星期缩写（Sun.，Mon.,Tues.，..）</td></tr><tr><td>%w</td><td>以数字表示周中的天数(0=Sunday,1=Monday....)</td><td></td><td></td></tr><tr><td>%j</td><td>以3位数字表示年中的天数(001,002...)</td><td>%U</td><td>以数字表示年中的第几周，（1,2,3。。）其中Sunday为周中第一天</td></tr><tr><td>%u</td><td>以数字表示年中的第几周，（1,2,3。。）其中Monday为周中第一天</td><td></td><td></td></tr><tr><td>%T</td><td>24小时制</td><td>%r</td><td>12小时制</td></tr><tr><td>%p</td><td>AM或PM</td><td>%%</td><td>表示%</td></tr></tbody></table><h3 id="流程函数" tabindex="-1"><a class="header-anchor" href="#流程函数" aria-hidden="true">#</a> 流程函数</h3><table><thead><tr><th>函数</th><th>用法</th></tr></thead><tbody><tr><td><code>IF(value,t ,f)</code></td><td>如果value是真，返回t，否则返回f</td></tr><tr><td><code>IFNULL(value1, value2) </code></td><td>如果value1不为空，返回value1，否则返回value2</td></tr><tr><td><code>CASE WHEN 条件1 THEN result1 WHEN 条件2 THEN result2 .... [ELSE resultn] END</code></td><td>相当于Java的if...else if...else...</td></tr><tr><td><code>CASE expr WHEN 常量值1 THEN 值1 WHEN 常量值1 THEN 值1 .... [ELSE 值n] END</code></td><td>相当于Java的switch...case...</td></tr></tbody></table><h3 id="其他函数" tabindex="-1"><a class="header-anchor" href="#其他函数" aria-hidden="true">#</a> 其他函数</h3><table><thead><tr><th style="text-align:left;">函数</th><th style="text-align:left;">用法</th></tr></thead><tbody><tr><td style="text-align:left;"><code>database()</code></td><td style="text-align:left;">返回当前数据库名</td></tr><tr><td style="text-align:left;"><code>version()</code></td><td style="text-align:left;">返回当前数据库版本</td></tr><tr><td style="text-align:left;"><code>user() </code></td><td style="text-align:left;">返回当前登录用户名</td></tr><tr><td style="text-align:left;"><code>password(str) </code></td><td style="text-align:left;">返回字符串str的加密版本，41位长的字符串</td></tr><tr><td style="text-align:left;"><code>md5(str)</code></td><td style="text-align:left;">返回字符串str的md5值，也是一种加密方式</td></tr></tbody></table>',27),o=[h];function s(c,l){return d(),e("div",null,o)}const E=t(a,[["render",s],["__file","函数.html.vue"]]);export{E as default};
